mapper.insert:
INSERT	INTO MEMBER (
	MEMBER_ID
	,MEMBER_EMAIL
	,MEMBER_PASSWORD
	,MEMBER_NICKNAME
	,MEMBER_NAME
	,MEMBER_SEX
	,MEMBER_REGIST_DATE
	,MEMBER_PROFILE_NAME
)
VALUES (
	MEMBER_ID_SEQ.NEXTVAL
	, #{memberEmail}
	, #{memberPassword}
	, #{memberNickname}
	, #{memberName}
	, #{memberSex}
	, SYSDATE+9/24
	, #{memberProfileName}
)

mapper.select:
SELECT	COUNT(MEMBER_ID)
FROM	MEMBER
WHERE	MEMBER_EMAIL = #{memberEmail}

SELECT	COUNT(MEMBER_ID)
FROM	MEMBER
WHERE	MEMBER_NICKNAME = #{memberNickname}

mapper.update:
UPDATE	MEMBER
SET		MEMBER_NICKNAME = #{memberNickname}
		,MEMBER_PROFILE_NAME = #{memberProfileName}
WHERE	MEMBER_ID = #{memberId}

UPDATE	MEMBER
SET		MEMBER_STYLE_1 = #{memberStyle1}
		,MEMBER_STYLE_2 = #{memberStyle2}
		,MEMBER_STYLE_3 = #{memberStyle3}
		,MEMBER_STYLE_4 = #{memberStyle4}
		,MEMBER_STYLE_5 = #{memberStyle5}
WHERE 	MEMBER_ID = #{memberId}
